// src/App.tsx
import React, { lazy, Suspense, useEffect, useState, createContext } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { SignUp, SignIn , useUser } from '@clerk/clerk-react';
import { CartProvider } from './context/CartContext';
import Header from './components/Header';
import AdminHeader from './components/AdminHeader';
import Footer from './components/Footer';
import { apiService } from './services/apiService';
import { Category, Product } from './types';
import { AdminRoute } from './routes/AdminRoute';
import { UserRoute } from './routes/UserRoute';
import AuthRedirect from './components/AuthRedirect'; 
import AdminCategories from './pages/admin/AdminCategories';
import AdminDashboard from './pages/admin/AdminHome';
import AdminLayout from './components/AdminLayout';
// --- AppContext.ts ---
export const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const AdminHome = lazy(() => import('./pages/admin/AdminHome'));

interface AppContextType {
  categories: Category[];
  featuredProducts: Product[];
  loading: boolean;
  error: string | null;
}

export const AppContext = createContext<AppContextType>({
  categories: [],
  featuredProducts: [],
  loading: true,
  error: null,
});

// --- Lazy pages ---
const Home = lazy(() => import('./pages/Home'));
const Categories = lazy(() => import('./pages/Categories'));
const Products = lazy(() => import('./pages/Products'));
const ProductDetail = lazy(() => import('./pages/ProductDetail'));
const Cart = lazy(() => import('./pages/Cart'));
const Checkout = lazy(() => import('./pages/checkout'));
const OrderSuccess = lazy(() => import('./pages/OrderSuccess'));
const Orders =lazy(() => import('./pages/Orders'));
// --- ScrollToTop ---
const ScrollToTop: React.FC = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
};

// --- LoadingSpinner ---
const LoadingSpinner: React.FC = () => (
  <div className="min-h-screen flex items-center justify-center">
    <div className="animate-spin rounded-full h-16 w-16 border-4 border-blue-500 border-t-transparent"></div>
  </div>
);

// Composant pour gÃ©rer dynamiquement le header
// Dans App.tsx - Composant DynamicHeader modifiÃ©
const DynamicHeader = () => {
  const location = useLocation();
  const { user, isLoaded } = useUser(); // ðŸ‘ˆ Ajouter useUser

  const isAdminPage = location.pathname.startsWith('/admin');

  // Ne montrer AdminHeader que si l'utilisateur est admin ET sur une page admin
  if (isAdminPage && isLoaded && user?.publicMetadata?.role === 'admin') {
    return <AdminHeader />;
  }

  // Sinon, montrer le Header normal
  return <Header />;
};
// Composant pour gÃ©rer dynamiquement le footer
const DynamicFooter = () => {
  const location = useLocation();
  const isAdminPage = location.pathname.startsWith('/admin');

  return isAdminPage ? null : <Footer />;
};

// --- App ---
function App() {
  const [appData, setAppData] = useState<AppContextType>({
    categories: [],
    featuredProducts: [],
    loading: true,
    error: null,
  });

  useEffect(() => {
    const fetchAppData = async () => {
      try {
        const [categories, products] = await Promise.all([
          apiService.getCategories(),
          apiService.getProducts().then((prods: Product[]) => prods.filter((p: Product) => p.featured).slice(0, 6)),
        ]);
        setAppData({ categories, featuredProducts: products, loading: false, error: null });
      } catch (err) {
        setAppData(prev => ({ ...prev, loading: false, error: (err as Error).message }));
      }
    };
    fetchAppData();
  }, []);

  return (
    <AppContext.Provider value={appData}>
      <CartProvider>
        <Router>
            <AuthRedirect />
          <ScrollToTop />
          <div className="min-h-screen flex flex-col bg-white">
            {/* Header dynamique */}
            <DynamicHeader />

            <main className="flex-grow">
              <Suspense fallback={<LoadingSpinner />}>
                <Routes>
                  {/* Routes publiques */}
                  <Route
                    path="/"
                    element={<Home categories={appData.categories} featuredProducts={appData.featuredProducts} />}
                  />
                  <Route path="/categories" element={<Categories />} />
                  <Route path="/categories/:slug" element={<Categories />} />
                  <Route path="/products" element={<Products />} />
                  <Route path="/product/:id" element={<ProductDetail />} />
                  <Route path="/cart" element={<Cart />} />
                  <Route path="/checkout" element={<Checkout />} />
                  <Route path="/order-success" element={<OrderSuccess />} />
                  <Route path="/about" element={<AboutPage />} />
                  <Route path="/contact" element={<ContactPage />} />

                  {/* Routes d'authentification */}
                  <Route path="/register/*" element={
                    <div className="flex justify-center items-center min-h-[80vh] py-8">
                      <SignUp 
                        routing="path" 
                        path="/register" 
                        afterSignUpUrl="/auth-redirect" // ðŸ‘ˆ ChangÃ© ici
                        signInUrl="/login"
                      />
                    </div>
                  } />

                  <Route path="/login/*" element={
                    <div className="flex justify-center items-center min-h-[80vh] py-8">
                      <SignIn 
                        routing="path" 
                        path="/login" 
                        afterSignUpUrl="/auth-redirect" // ðŸ‘ˆ ChangÃ© ici
                        signUpUrl="/register"
                      />
                    </div>
                  } />

                  {/* Route de redirection aprÃ¨s auth */}
                  <Route path="/auth-redirect" element={<AuthRedirect />} />

                  {/* Route admin protÃ©gÃ©e */}
                  <Route path="/admin/*" element={
                    <AdminRoute>
                      <AdminHome />
                    </AdminRoute>
                  } />
                  // Routes Admin
<Route path="/admin" element={
  <AdminLayout>
    <AdminDashboard />
  </AdminLayout>
} />
<Route path="/admin/products" element={
  <AdminLayout>
    <AdminProducts />
  </AdminLayout>
} />
<Route path="/admin/categories" element={
  <AdminLayout>
    <AdminCategories />
  </AdminLayout>
} />
<Route path="/admin/orders" element={
  <AdminLayout>
    <AdminOrders />
  </AdminLayout>
} />
<Route path="/admin/users" element={
  <AdminLayout>
    <AdminUsers />
  </AdminLayout>
} />

                  {/* Routes utilisateur protÃ©gÃ©es */}
                  <Route path="/profile" element={
                    <UserRoute>
                      <div className="min-h-[60vh] flex items-center justify-center">
                        <div className="text-center">
                          <h1 className="text-3xl font-bold text-gray-900 mb-4">Mon Profil</h1>
                          <p className="text-gray-600">Page de profil utilisateur</p>
                        </div>
                      </div>
                    </UserRoute>
                  } />
<Route path="/orders" element={
  <UserRoute>
    <Orders />
  </UserRoute>
} />
                  {/* Route 404 */}
                  <Route path="*" element={
                    <div className="min-h-[60vh] flex items-center justify-center">
                      <div className="text-center">
                        <h1 className="text-4xl font-bold text-gray-900 mb-4">404</h1>
                        <p className="text-xl text-gray-600">Page non trouvÃ©e</p>
                      </div>
                    </div>
                  } />
                </Routes>
              </Suspense>
            </main>

            {/* Footer dynamique */}
            <DynamicFooter />
          </div>
        </Router>
      </CartProvider>
    </AppContext.Provider>
  );
}
// Composants de pages statiques sÃ©parÃ©s pour une meilleure organisation
const AboutPage: React.FC = () => (
  <>
    {/* Google tag (gtag.js) */}
    <script async src="https://www.googletagmanager.com/gtag/js?id=G-393HMHQQSE"></script>
    <script>
      {`
        window.dataLayer = window.dataLayer || [];
        function gtag(){dataLayer.push(arguments);}
        gtag('js', new Date());
        gtag('config', 'G-393HMHQQSE');
      `}
    </script>

    <div className="min-h-screen bg-angel-background">
      <div className="container mx-auto px-4">
        <div className="bg-gradient-angel py-16">
          <div className="text-center max-w-3xl mx-auto">
            <h1 className="font-tan-pearl text-5xl text-angel-light mb-4">Ã€ Propos de Nous</h1>
            <div className="w-24 h-1 bg-angel-pink mx-auto mb-8"></div>
            <p className="text-xl text-angel-light max-w-2xl mx-auto">
              L'art de l'Ã©lÃ©gance personnalisÃ©e
            </p>
          </div>
        </div>

        <div className="max-w-6xl mx-auto py-8">
          <div className="bg-angel-card rounded-2xl shadow-sm p-8 md:p-12 border border-angel-border mb-12">
            <div className="grid md:grid-cols-2 gap-12 items-center">
              <div>
                <h2 className="font-tan-pearl text-3xl text-primary mb-6">Notre Histoire</h2>
                <p className="text-angel-dark text-lg leading-relaxed mb-6">
                  FondÃ© en 2023, Angel's Bags est nÃ© d'une passion pour l'artisanat d'exception 
                  et le dÃ©sir de crÃ©er des accessoires uniques qui racontent une histoire. 
                  Chaque sac est une Å“uvre d'art, mÃ©ticuleusement conÃ§u Ã  la main avec des 
                  perles et cristaux soigneusement sÃ©lectionnÃ©s.
                </p>
                <p className="text-angel-dark text-lg leading-relaxed">
                  Notre mission est d'offrir Ã  chaque cliente un accessoire qui reflÃ¨te 
                  sa personnalitÃ©, alliant tradition artisanale et design contemporain.
                </p>
              </div>
              <div className="bg-angel-light rounded-xl p-8 text-center border border-angel-border">
                <div className="text-4xl mb-4">âœ¨</div>
                <h3 className="font-semibold text-primary text-xl mb-4">Notre Philosophie</h3>
                <p className="text-angel-dark">
                  Croire que chaque femme mÃ©rite un accessoire aussi unique qu'elle l'est
                </p>
              </div>
            </div>
