import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { Helmet } from 'react-helmet';
import { useUser } from '@clerk/clerk-react';

type DeliveryMethod = 'delivery' | 'pickup';
type PaymentMethod = 'whatsapp' | 'online' | 'cash';

declare global {
  interface Window {
    gtag?: (...args: any[]) => void;
  }
}

interface FormData {
  fullName: string;
  phone: string;
  email: string;
  address: string;
  city: string;
  postalCode: string;
  notes: string;
}

interface CartItem {
  id: string;
  quantity: number;
  selectedColor?: string;
  selectedSize?: string;
  personalization?: string;
  product: {
    id: string;
    name: string;
    price: number;
    images?: string[];
  };
}

const Checkout: React.FC = () => {
  const navigate = useNavigate();
  const { user, isSignedIn } = useUser();
  
  const [deliveryMethod, setDeliveryMethod] = useState<DeliveryMethod>('delivery');
  const [paymentMethod, setPaymentMethod] = useState<PaymentMethod>('whatsapp');
  const { cart, getCartTotal, clearCart } = useCart() as {
    cart: { items: CartItem[] };
    getCartTotal: () => number;
    clearCart: () => void;
  };

  const [formData, setFormData] = useState<FormData>({
    fullName: '',
    phone: '',
    email: user?.primaryEmailAddress?.emailAddress || '',
    address: '',
    city: '',
    postalCode: '',
    notes: ''
  });

  const deliveryFee = deliveryMethod === 'delivery' ? 7 : 0;
  const total = getCartTotal() + deliveryFee;

  // Rediriger vers login si non connectÃ©
  React.useEffect(() => {
    if (!isSignedIn) {
      navigate('/login?redirect=checkout');
    }
  }, [isSignedIn, navigate]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Fonction pour sauvegarder la commande dans MongoDB
  // Dans Checkout.tsx - amÃ©liorer la fonction saveOrderToMongoDB
const saveOrderToMongoDB = async (orderData: any) => {
  try {
    console.log('ðŸ“¦ DonnÃ©es envoyÃ©es Ã  MongoDB:', JSON.stringify(orderData, null, 2));
    
    const response = await fetch('http://localhost:5000/api/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(orderData),
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Erreur lors de la sauvegarde de la commande');
    }

    const result = await response.json();
    console.log('âœ… Commande sauvegardÃ©e:', result);
    return result;
  } catch (error) {
    console.error('âŒ Erreur sauvegarde commande:', error);
    throw error; // Propager l'erreur pour la gÃ©rer dans handleSubmit
  }
};

  const formatWhatsAppMessage = () => {
    let message = `ðŸ›ï¸ *NOUVELLE COMMANDE - Angel's Bags*\n\n`;
    message += `ðŸ‘¤ *Client:* ${formData.fullName}\n`;
    message += `ðŸ“± *TÃ©lÃ©phone:* ${formData.phone}\n`;
    message += `ðŸ“§ *Email:* ${formData.email}\n`;
    message += `ðŸ†” *ID Client:* ${user?.id || 'Non connectÃ©'}\n\n`;
    
    message += `ðŸ“¦ *Mode de livraison:* ${deliveryMethod === 'delivery' ? 'Livraison Ã  domicile' : 'Retrait en magasin'}\n`;
    
    if (deliveryMethod === 'delivery') {
      message += `ðŸ“ *Adresse:* ${formData.address}\n`;
      message += `ðŸ™ï¸ *Ville:* ${formData.city}\n`;
      message += `ðŸ“® *Code postal:* ${formData.postalCode}\n`;
    }
    
    message += `\nðŸ’³ *Mode de paiement:* `;
    if (paymentMethod === 'whatsapp') message += 'Confirmation par WhatsApp';
    else if (paymentMethod === 'online') message += 'Paiement en ligne';
    else message += 'Paiement Ã  la livraison';
    
    message += `\n\nðŸ›’ *PRODUITS:*\n`;
    cart.items.forEach((item: any, index: number) => {
      message += `\n${index + 1}. *${item.product.name}*\n`;
      message += `   QuantitÃ©: ${item.quantity}\n`;
      message += `   Prix unitaire: ${item.product.price} TND\n`;
      if (item.selectedColor) message += `   Couleur: ${item.selectedColor}\n`;
      if (item.selectedSize) message += `   Taille: ${item.selectedSize}\n`;
      if (item.personalization) message += `   Personnalisation: "${item.personalization}"\n`;
      message += `   Sous-total: ${(item.product.price * item.quantity).toFixed(2)} TND\n`;
    });
    
    message += `\nðŸ’° *TOTAL:*\n`;
    message += `Sous-total: ${getCartTotal().toFixed(2)} TND\n`;
    message += `Frais de livraison: ${deliveryFee.toFixed(2)} TND\n`;
    message += `*TOTAL Ã€ PAYER: ${total.toFixed(2)} TND*\n`;
    
    if (formData.notes) {
      message += `\nðŸ“ *Notes:* ${formData.notes}\n`;
    }
    
    return encodeURIComponent(message);
  };

 // Dans Checkout.tsx - modifier la fonction handleSubmit
const handleSubmit = async (e: React.FormEvent) => {
  e.preventDefault();

  // VÃ©rifier si l'utilisateur est connectÃ©
  if (!isSignedIn || !user) {
    alert('Veuillez vous connecter pour finaliser votre commande');
    navigate('/login?redirect=checkout');
    return;
  }

  // Validation
  if (!formData.fullName || !formData.phone) {
    alert('Veuillez remplir les champs obligatoires');
    return;
  }
  
  if (deliveryMethod === 'delivery' && (!formData.address || !formData.city)) {
    alert('Veuillez remplir l\'adresse de livraison');
    return;
  }

  // PrÃ©parer les donnÃ©es de la commande pour MongoDB
  const orderData = {
    userId: user.id,
    userEmail: user.primaryEmailAddress?.emailAddress || formData.email,
    customerInfo: {
      fullName: formData.fullName,
      phone: formData.phone,
      email: formData.email || user.primaryEmailAddress?.emailAddress || '',
    },
    deliveryInfo: {
      method: deliveryMethod,
      address: deliveryMethod === 'delivery' ? {
        address: formData.address,
        city: formData.city,
        postalCode: formData.postalCode,
      } : null,
    },
    paymentInfo: {
      method: paymentMethod,
      total: total,
      currency: 'TND'
    },
    items: cart.items.map(item => ({
      productId: item.product.id, // âœ… S'assurer que productId est prÃ©sent
      productName: item.product.name,
      quantity: item.quantity,
      price: item.product.price,
      selectedColor: item.selectedColor,
      selectedSize: item.selectedSize,
      personalization: item.personalization,
      subtotal: item.product.price * item.quantity // âœ… Calculer le subtotal
    })),
    status: 'pending',
    notes: formData.notes
  };

  // --- EVENT GOOGLE ANALYTICS ---
  if (window.gtag) {
    window.gtag('event', 'purchase_attempt', {
      method: paymentMethod,
      value: total,
      currency: 'TND',
      items: cart.items.map((item) => ({
        item_name: item.product.name,
        item_id: item.product.id,
        price: item.product.price,
        quantity: item.quantity
      }))
    });
  }

  try {
    // Sauvegarder la commande dans MongoDB
    const savedOrder = await saveOrderToMongoDB(orderData);
    
    // Traitement commande
    if (paymentMethod === 'whatsapp') {
      const whatsappNumber = '21646535386';
      const message = formatWhatsAppMessage();
      window.open(`https://wa.me/${whatsappNumber}?text=${message}`, '_blank');
      setTimeout(() => {
        clearCart();
        navigate('/order-success');
      }, 2000);
    } else if (paymentMethod === 'online') {
      alert('Le paiement en ligne sera disponible prochainement !');
    } else {
      const whatsappNumber = '21646535386';
      const message = formatWhatsAppMessage();
      window.open(`https://wa.me/${whatsappNumber}?text=${message}`, '_blank');
      setTimeout(() => {
        clearCart();
        navigate('/order-success');
      }, 2000);
    }
  } catch (error) {
    console.error('Erreur lors du traitement de la commande:', error);
    alert('Une erreur est survenue lors du traitement de votre commande. Veuillez rÃ©essayer.');
  }
};

  if (!isSignedIn) {
    return (
      <div className="min-h-screen bg-angel-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-angel-gold border-t-transparent mx-auto mb-4"></div>
          <p className="text-angel-dark">VÃ©rification de connexion...</p>
        </div>
      </div>
    );
  }

  if (cart.items.length === 0) {
    navigate('/cart');
    return null;
  }

  return (
    <>
      {/* === BALISES SEO OPTIMISÃ‰ES === */}
      <Helmet>
        <title>{`Finaliser Commande - Paiement SÃ©curisÃ© | Angel's Bags`}</title>
        
        <meta 
          name="description" 
          content="ðŸ›’ Finalisez votre commande de sacs en perles et cristal faits main. Livraison Tunisie, paiement WhatsApp sÃ©curisÃ©. Processus simple et rapide pour vos sacs Angel's Bags." 
        />
        
        <meta 
          name="keywords" 
          content="commander sacs perles, paiement Angel's Bags, checkout sacs cristal, livraison Tunisie, WhatsApp paiement, sacs faits main commande, finaliser achat Angel's Bags" 
        />
        
        <meta property="og:title" content="Finaliser Votre Commande - Angel's Bags" />
        <meta property="og:description" content="Finalisez votre commande de sacs en perles et cristal faits main. Paiement sÃ©curisÃ© et livraison en Tunisie." />
        <meta property="og:url" content="https://angelsbags.netlify.app/checkout" />
        <meta property="og:type" content="website" />
        
        <meta name="twitter:card" content="summary" />
        <meta name="twitter:title" content="Finaliser Commande - Angel's Bags" />
        <meta name="twitter:description" content="Passez commande de vos sacs en perles et cristal faits main" />
        
        <link rel="canonical" href="https://angelsbags.netlify.app/checkout" />
        
        {/* Schema.org pour CheckoutPage */}
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "CheckoutPage",
            "name": "Finaliser la Commande - Angel's Bags",
            "description": "Page de finalisation de commande pour les sacs en perles et cristal faits main",
            "url": "https://angelsbags.netlify.app/checkout",
            "potentialAction": {
              "@type": "OrderAction",
              "target": "https://angelsbags.netlify.app/order-success"
            }
          })}
        </script>
      </Helmet>

      <div className="min-h-screen bg-angel-background py-8">
        <div className="container mx-auto px-4">
          {/* === H1 OPTIMISÃ‰ === */}
          <h1 className="font-tan-pearl text-4xl text-primary mb-2">
            Finaliser Votre Commande
          </h1>
          <p className="text-angel-dark text-lg mb-8 max-w-3xl">
            ComplÃ©tez vos informations pour recevoir vos <strong>sacs en perles et cristal faits main</strong>. 
            Processus sÃ©curisÃ© et confirmation rapide par WhatsApp.
          </p>
          
          {/* Indication utilisateur connectÃ© */}
          <div className="bg-green-50 rounded-xl p-4 mb-6 border border-green-200">
            <div className="flex items-center space-x-3">
              <svg className="w-5 h-5 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
              </svg>
              <div>
                <p className="text-sm text-green-800 font-medium">
                  ConnectÃ© en tant que: {user.primaryEmailAddress?.emailAddress}
                </p>
                <p className="text-xs text-green-600">
                  Votre commande sera associÃ©e Ã  votre compte
                </p>
              </div>
            </div>
          </div>
          
          <form onSubmit={handleSubmit} className="grid grid-cols-1 lg:grid-cols-3 gap-8">
