// src/App.tsx
import React, { lazy, Suspense, useEffect, useState, createContext } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import {  SignUp, SignIn } from '@clerk/clerk-react';
import { CartProvider } from './context/CartContext';
import Header from './components/Header';
import Footer from './components/Footer';
import { apiService } from './services/apiService';
import { Category, Product } from './types';

// --- AppContext.ts ---
export const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

interface AppContextType {
  categories: Category[];
  featuredProducts: Product[];
  loading: boolean;
  error: string | null;
}

export const AppContext = createContext<AppContextType>({
  categories: [],
  featuredProducts: [],
  loading: true,
  error: null,
});

// --- Lazy pages ---
const Home = lazy(() => import('./pages/Home'));
const Categories = lazy(() => import('./pages/Categories'));
const Products = lazy(() => import('./pages/Products'));
const ProductDetail = lazy(() => import('./pages/ProductDetail'));
const Cart = lazy(() => import('./pages/Cart'));
const Checkout = lazy(() => import('./pages/checkout'));
const OrderSuccess = lazy(() => import('./pages/OrderSuccess'));
const Register = lazy(() => import('./pages/Register'));
const LoginPage = lazy(() => import('./pages/Login'));

// --- ScrollToTop ---
const ScrollToTop: React.FC = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
};

// --- LoadingSpinner ---
const LoadingSpinner: React.FC = () => (
  <div className="min-h-screen flex items-center justify-center">
    <div className="animate-spin rounded-full h-16 w-16 border-4 border-blue-500 border-t-transparent"></div>
  </div>
);

// --- App ---
function App() {
  const [appData, setAppData] = useState<AppContextType>({
    categories: [],
    featuredProducts: [],
    loading: true,
    error: null,
  });

  useEffect(() => {
    const fetchAppData = async () => {
      try {
        const [categories, products] = await Promise.all([
          apiService.getCategories(),
          apiService.getProducts().then((prods: Product[]) => prods.filter((p: Product) => p.featured).slice(0, 6)),
        ]);
        setAppData({ categories, featuredProducts: products, loading: false, error: null });
      } catch (err) {
        setAppData(prev => ({ ...prev, loading: false, error: (err as Error).message }));
      }
    };
    fetchAppData();
  }, []);

  return (
      <AppContext.Provider value={appData}>
        <CartProvider>
          <Router>
            <ScrollToTop />
            <div className="min-h-screen flex flex-col bg-white">
              <Header />
              <main className="flex-grow">
                <Suspense fallback={<LoadingSpinner />}>
                  <Routes>
                    {/* Routes classiques */}
                    <Route
                      path="/"
                      element={<Home categories={appData.categories} featuredProducts={appData.featuredProducts} />}
                    />
                    <Route path="/categories" element={<Categories />} />
                    <Route path="/categories/:slug" element={<Categories />} />
                    <Route path="/products" element={<Products />} />
                    <Route path="/product/:id" element={<ProductDetail />} />
                    <Route path="/cart" element={<Cart />} />
                    <Route path="/checkout" element={<Checkout />} />
                    <Route path="/order-success" element={<OrderSuccess />} />

             <Route path="/register/*"  element={
    <SignUp
      routing="path"
      path="/register"
      afterSignUpUrl="/" // ou /home
      redirectUrl="/" // facultatif
    />
  }  />
                    <Route path="/login/*" element={
    <SignIn
      routing="path"
      path="/login"
       afterSignUpUrl="/" 
      redirectUrl="/"
    />
  }  />
                  </Routes>
                </Suspense>
              </main>
              <Footer />
            </div>
          </Router>
        </CartProvider>
      </AppContext.Provider>
  );
}


