import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { CartProvider } from './context/CartContext';
import Header from './components/Header';
import Footer from './components/Footer'; 
import Home from './pages/Home';
import Categories from './pages/Categories';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Register from './pages/Register'; 
import { Category, Product } from './types';
import { apiService } from './services/apiService';

export const API_BASE_URL = process.env.REACT_APP_API_URL || 'https://angels-bags-7c1t.onrender.com';

interface AppContextType {
  categories: Category[];
  featuredProducts: Product[];
  loading: boolean;
  error: string | null;
}

export const AppContext = React.createContext<AppContextType>({
  categories: [],
  featuredProducts: [],
  loading: true,
  error: null,
});

function App() {
  const [appData, setAppData] = useState<AppContextType>({
    categories: [],
    featuredProducts: [],
    loading: true,
    error: null,
  });

  useEffect(() => {
    const fetchAppData = async () => {
      try {
        console.log('üîÑ Chargement des donn√©es depuis l\'API...');
        console.log('üìç URL API:', API_BASE_URL);

        const [categories, products] = await Promise.all([
          apiService.getCategories(),
          apiService.getProducts()
        ]);

        const featuredProducts = products.filter((product: Product) => product.featured);

        setAppData({
          categories,
          featuredProducts,
          loading: false,
          error: null,
        });

        console.log('‚úÖ Donn√©es charg√©es avec succ√®s:', {
          categories: categories.length,
          products: products.length,
          featured: featuredProducts.length
        });

      } catch (error) {
        console.error('‚ùå Erreur lors du chargement des donn√©es:', error);
        setAppData(prev => ({
          ...prev,
          loading: false,
          error: error instanceof Error ? error.message : 'Erreur inconnue'
        }));
      }
    };

    fetchAppData();
  }, []);

  // Composant de chargement
  if (appData.loading) {
    return (
      <div className="min-h-screen bg-angel-background flex flex-col items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-angel-border border-t-angel-gold mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold text-primary mb-2">Chargement d'Angel's Bags</h2>
          <p className="text-angel-dark">Connexion √† la base de donn√©es...</p>
        </div>
      </div>
    );
  }

  // Composant d'erreur
  if (appData.error) {
    return (
      <div className="min-h-screen bg-angel-background flex flex-col items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-xl font-semibold text-primary mb-4">Erreur de connexion</h2>
          <p className="text-angel-dark mb-4">{appData.error}</p>
          <div className="bg-angel-card p-4 rounded-lg border border-angel-border text-sm text-left">
            <p className="mb-2"><strong>V√©rifiez que :</strong></p>
            <ul className="list-disc list-inside space-y-1">
              <li>Le serveur backend est d√©marr√©</li>
              <li>L'URL de l'API est correcte</li>
              <li>MongoDB Atlas est connect√©</li>
            </ul>
          </div>
          <button 
            onClick={() => window.location.reload()}
            className="mt-6 bg-angel-gold text-white px-6 py-2 rounded-lg hover:bg-primary transition-all"
          >
            R√©essayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <AppContext.Provider value={appData}>
      <CartProvider>
        <Router>
          <div className="min-h-screen bg-white flex flex-col">
            <Header />
            <main className="flex-grow">
              <Routes>
                <Route 
                  path="/" 
                  element={
                    <Home 
                      categories={appData.categories}
                      featuredProducts={appData.featuredProducts}
                    />
                  } 
                />
                <Route 
                  path="/categories" 
                  element={<Categories />} 
                />
                <Route 
                  path="/categories/:slug" 
                  element={<Categories />} 
                />
                <Route 
                  path="/products" 
                  element={<Products />} 
                />
                <Route path="/product/:id" element={<ProductDetail />} />
                <Route path="/cart" element={<Cart />} />

                <Route path="/register" element={<Register />} />

                <Route 
                  path="/about" 
                  element={
                    <div className="min-h-screen py-16 bg-angel-background">
                      <div className="container mx-auto px-4">
                        <h1 className="font-tan-pearl text-4xl text-center text-primary mb-8">√Ä Propos d'Angel's Bags</h1>
                        <div className="max-w-3xl mx-auto bg-angel-card rounded-2xl shadow-sm p-8 border border-angel-border">
                          <p className="text-angel-dark text-lg leading-relaxed mb-6">
                            Angel's Bags incarne l'excellence artisanale dans la cr√©ation de sacs 
                            uniques en perles et cristal. Chaque pi√®ce est con√ßue avec passion 
                            et attention aux d√©tails pour vous offrir des accessoires d'exception.
                          </p>
                          <div className="grid md:grid-cols-2 gap-6 mt-8">
                            <div className="text-center p-6 bg-angel-pink rounded-xl border border-angel-border">
                              <h3 className="font-semibold text-primary mb-2 text-lg">Fait Main</h3>
                              <p className="text-angel-dark text-sm">Chaque sac est unique et r√©alis√© artisanalement</p>
                            </div>
                            <div className="text-center p-6 bg-angel-light rounded-xl border border-angel-border">
                              <h3 className="font-semibold text-primary mb-2 text-lg">Mat√©riaux Premium</h3>
                              <p className="text-angel-dark text-sm">Perles de qualit√© et cristaux Swarovski¬Æ</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  } 
                />
                <Route 
                  path="/contact" 
                  element={
                    <div className="min-h-screen py-16 bg-angel-background">
                      <div className="container mx-auto px-4">
                        <h1 className="font-tan-pearl text-4xl text-center text-primary mb-8">Contact</h1>
                        <div className="max-w-2xl mx-auto bg-angel-card rounded-2xl shadow-sm p-8 border border-angel-border">
                          <div className="space-y-4">
                            <div className="flex items-center space-x-3 p-4 bg-angel-pink rounded-xl">
                              <svg className="w-6 h-6 text-angel-gold" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                              </svg>
                              <span className="text-primary font-medium">contact@angels-bags.com</span>
                            </div>
                            <div className="flex items-center space-x-3 p-4 bg-angel-light rounded-xl">
                              <svg className="w-6 h-6 text-angel-gold" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                              </svg>
                              <span className="text-primary font-medium">+216 46 535 386</span>
                            </div>
                            <div className="flex items-center space-x-3 p-4 bg-angel-pink rounded-xl">
                              <svg className="w-6 h-6 text-angel-gold" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                              </svg>
                              <span className="text-primary font-medium">La Mannouba, Tunis</span>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  } 
                />
                <Route 
                  path="/login" 
                  element={
                    <div className="min-h-screen py-16 bg-angel-background">
                      <div className="container mx-auto px-4">
                        <h1 className="font-tan-pearl text-4xl text-center text-primary mb-8">Connexion</h1>
                        <div className="max-w-md mx-auto bg-angel-card rounded-2xl shadow-sm p-8 border border-angel-border">
                          <form className="space-y-6">
                            <div>
                              <label className="block text-sm font-semibold text-primary mb-2">
                                Email
                              </label>
                              <input 
                                type="email" 
                                className="w-full px-4 py-3 border border-angel-border rounded-xl focus:outline-none focus:ring-2 focus:ring-angel-gold focus:border-angel-gold transition-all bg-white"
                                placeholder="votre@email.com"
                              />
                            </div>
                            <div>
                              <label className="block text-sm font-semibold text-primary mb-2">
                                Mot de passe
                              </label>
                              <input 
                                type="password" 
                                className="w-full px-4 py-3 border border-angel-border rounded-xl focus:outline-none focus:ring-2 focus:ring-angel-gold focus:border-angel-gold transition-all bg-white"
                                placeholder="Votre mot de passe"
                              />
                            </div>
                            <button 
                              type="submit"
                              className="w-full bg-angel-gold text-white py-3 rounded-xl hover:bg-primary transition-all font-semibold"
                            >
                              Se connecter
                            </button>
                          </form>
                          <div className="mt-6 text-center">
                            <p className="text-angel-dark text-sm">
                              Pas de compte ?{' '}
                              <a href="/register" className="text-angel-gold hover:underline font-semibold">
                                Cr√©er un compte
                              </a>
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  } 
                />
              </Routes>
            </main>
            <Footer /> 
          </div>
        </Router>
      </CartProvider>
    </AppContext.Provider>
  );
}

export default App;
